import React from "react";

class Signin extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			signInEmail: '',
			signInPassword: '',
		}
	}

	onEmailChange = (event) => {
		this.setState({signInEmail: event.target.value})
	}
	onPasswordChange = (event) => {
		this.setState({signInPassword: event.target.value})
	}
	onSubmitSignIn = () => {
		console.log(this.state);
		this.props.onRouteChange("home");
	}

	render() {
	const { onRouteChange } = this.props;
	return (
		<article className=" center br3 shadow-5 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw6 center">	
			<main className="pa4 bg-transparent white-80">
			  <div className="measure">
			    <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
			      <legend className="f1 fw6 ph0 mh0">Sign In</legend>
			      <div className="mt3">
			        <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
			        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address" />
			      </div>
			      <div className="mv3">
			        <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
			        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" />
			      </div>
			    </fieldset>
			    <div className="">
			      <input 
			      onClick= {this.onSubmitSignIn} 
			      className="b ph3 pv2 input-reset ba white b--black bg-transparent grow pointer f6 dib" 
			      type="submit" 
			      value="Sign in" />
			    </div>
			    <div className="lh-copy mt3">
			      <p onClick= {() => onRouteChange('register')} href="#0" className="f6 link dim white db pointer">Register</p>
			    </div>
			  </div>
			</main>
		</article>
		);
	}		
}

export default Signin;
